lst = []
for address in addresses:
    split_address = address.split()

    lst1 = []
    for i in split_address: 
        if not i.replace('-','').isdigit():
            i = i.upper()
            if i == 'ST':
                lst1.append('STREET')
            elif i == 'AVE':
                lst1.append('AVENUE')
            elif i == 'DR':
                lst1.append('DRIVE')
            else:
                lst1.append(i)       

    lst.append(" ".join([i for i in lst1]))
